$(document).ready(function(){examTable();$("#submitExam").submitform({url:"/gradeExam",callback:function(b){$(".scored").html(b.score+" - "+b.rate+"%");if(b.did_pass){showNotification("top","right","Congrats you passed!!","success")}else{showNotification("top","right","Sorry AHOOOOO!!","danger")}$(".sub").hide();$('input[type="radio"]').attr("disabled",true);for(var a=0;a<b.total_q;a++){var c=$('input[name="choice'+(a+1)+'"]:checked').val();if(c!=b.array[a]){$('input[name="choice'+(a+1)+'"]:checked').parent().html("<i style='color:red' class='fa fa-times'></i>")}else{$('input[name="choice'+(a+1)+'"]:checked').parent().html("<i style='color:green' class='fa fa-check'></i>")}}},})});function examTable(){$("#examTable").table({url:"/viewExamsAdmin",data:{per_page:5,},render:[function(a){return a.id},function(a){return a.title},function(a){return a.description},function(a){return a.passing},function(a){return(a.result!=null)?a.result:"N/A"},function(a){return'<a href="user/take/'+a.id+'" class="btn btn-sm mr-2 "><i class="fa fa-eye"></i></a>'},]})};